import sqlite3

conn = sqlite3.connect('data/database.db')
cursor = conn.cursor()

# --- Создание таблиц ---
cursor.execute('''
CREATE TABLE IF NOT EXISTS Сотрудники (
    ФИО TEXT,
    подразделение TEXT,
    должность TEXT,
    количество_ставок REAL,
    вид_занятости TEXT,
    категория_персонала TEXT,
    ученое_звание TEXT,
    ученая_степень TEXT,
    возраст INTEGER,
    оргструктура TEXT
)
''')

cursor.execute('''
CREATE TABLE IF NOT EXISTS Научные_работы (
    автор TEXT,
    название TEXT,
    второе_название TEXT,
    год INTEGER,
    авторы TEXT,
    ссылки TEXT,
    название_журнала TEXT,
    база_цитирования TEXT,
    ссылка TEXT
)
''')

# --- Удалим старые записи (для чистоты повторных запусков) ---
cursor.execute("DELETE FROM Сотрудники")
cursor.execute("DELETE FROM Научные_работы")

